    <style>
        .char1, .char2, .char3 {
        color: transparent; 
        border-right: .1em solid transparent; 
        -webkit-animation: typing .5s;
        -webkit-animation-fill-mode: forwards;
        -moz-animation: typing .5s;
        -moz-animation-fill-mode: forwards;
        }
        
        .char1 {-webkit-animation-delay: .5s;}
        .char2 {-webkit-animation-delay: 1s;}
        /* give your last character some padding and the blink animation */
        .char3 {            
        padding-right: 2px; 
        -webkit-animation: typing 1s;
        -webkit-animation: blink 1s infinite;
        -webkit-animation-delay: 1.5s;
        -moz-animation: typing 1s infinite;
        -moz-animation: blink 1s infinite;
        -moz-animation-delay: 1.5s;
        }
        
        /* typing animation */
        @-webkit-keyframes typing {
        from, to {color: white; border-color: transparent; }
        70% {color: white; border-color: #07D6FF; }
        }
        @-moz-keyframes typing {
        from, to {color: white; border-color: transparent; }
        70% {color: white; border-color: #07D6FF; }
        }
        
        /* the blinking faux cursor at the end */
        @-webkit-keyframes blink {
        from, to {color: white; border-color: transparent;}
        50% {border-color: #07D6FF;}
        }
        @-moz-keyframes blink {
        from, to {border-color: transparent;}
        50% {border-color: #07D6FF;}
        }
        @keyframes color {
        0% {
            background: #FDFDFD;
        }
        100% {
            background: #fb0;
        }
        }
        @keyframes width {
        0% {
            max-width: 100%;
        }
        100% {
            width: 15%;
        }
        }
        @keyframes widthadd {
        0% {
            width: 10px;
        }
        100% {
            width: 83px;
        }
        }
        @keyframes fly {
        0% {
            transform: translateX(-48%);
        }
        25% {
            transform: translateX(-58%);
        }
        50% {
            transform: translateX(-46%);
        }
        75% {
            transform: translateX(-55%);
        }
        100% {
            transform: translateX(-48%);
        }
        }
        @keyframes blind {
        0% {
            height: 0;
        }
        60% {
            height: 100%;
        }
        100% {
            height: 0;
        }
        }
