@push('js')
    <script>
        $(document).on('scroll', () => {
            let sticky = $('nav')
            if ($(document).scrollTop() >= $('nav').height()) {
                if (sticky.length == 0) return 
                if( $(document).scrollTop() >= sticky.offset().top ) {
                    sticky.css({ position: 'sticky', top: 0, 'box-shadow': '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)' })
                }
            } else {
                sticky.css({ position: 'relative', top: 0, 'box-shadow': 'none' })  
            }
        })
    </script>
@endpush